import React, {Component} from 'react';
import bridge_logo from './bridge_logo.png';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import './App.css';

class App extends Component {
  state = {
    response: ''
  };

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/api/mensagem');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={bridge_logo} className="App-logo" alt="logo" />
          <h1 className="App-title">Desafio Dev. Full Stack</h1>
        </header>
        <body className="App-body">
          <p className="App-intro">Calcule já os divisores de um número:</p>
          <TextField id="filled-basic" label="Filled" variant="filled" /><p className="newLine"/>
          <Button className="ButtonCalculate" variant="contained" color="secondary">Calcular</Button>

          <p className="App-intro">{this.state.response}</p>
        </body>
      </div>
    );
  }
}

export default App;
