import React, {Component} from 'react';
import bridge_logo from './bridge_logo.png';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import './App.css';

class App extends Component {
  state = {
    response: '',
    numberValue: NaN
  };

  // componentDidMount() {
  //   this.callApi()
  //     .then(res => this.setState({ response: res.express }))
  //     .catch(err => console.log(err));
  // }

  handleInputNumberChange = (e) => { 
    let input_value = parseInt(e.target.value)

    if (isNaN(input_value)) {
      this.setState({ numberValue: NaN })
    } else {
      this.setState({ numberValue: input_value })
    }
  }

  getTextField() {
    if (isNaN(this.state.numberValue)) {
      return <TextField error id="standard-error-helper-text" label="Erro" helperText="Isso não é um número inteiro." onChange={ this.handleInputNumberChange } />
    } else {
      return <TextField id="standard-basic" label="Insira um número inteiro" onChange={ this.handleInputNumberChange } />
    }
  }

  submitNumber = async () => {
    const response = await fetch('/api/divisors')
    const body = await response.json()

    if (response.status !== 200) {
      throw Error(body.message)
    }

    return body
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={bridge_logo} className="App-logo" alt="logo" />
          <h1 className="App-title">Desafio Dev. Full Stack</h1>
        </header>
        <div className="App-body">
          <p className="App-intro">Calcule já os divisores de um número:</p>
          { getTextField() }
          <Button className="ButtonCalculate" variant="contained" color="secondary" onClick={ this.validateInput }>Calcular</Button>

          <p className="App-intro">{this.state.response}</p>
        </div>
      </div>
    )
  }
}

export default App;
